useEffect(() => {
  if (!selectedMonth || csvData.length === 0) {
    setMonthlyTotal(0);
    return;
  }

  let total = 0;

  csvData.forEach((row) => {
    const rawDate = (row?.[0] ?? "").trim();     // "10-01-2026"
    const amountStr = (row?.[2] ?? "").toString().trim();

    // Skip header or invalid rows
    if (!rawDate.includes("-")) return;

    // Convert DD-MM-YYYY → YYYY-MM
    const parts = rawDate.split("-");
    if (parts.length !== 3) return;

    const [day, month, year] = parts;
    const formatted = `${year}-${month}`;  // "2026-01"

    if (formatted === selectedMonth) {
      const amt = parseFloat(amountStr);
      if (!isNaN(amt)) total += amt;
    }
  });

  setMonthlyTotal(total);
}, [selectedMonth, csvData]);
