import { useEffect, useState } from "react";
import Papa from "papaparse";

function MonthlySummary() {
  const [selectedMonth, setSelectedMonth] = useState("");
  const [monthlyTotal, setMonthlyTotal] = useState(0);
  const [csvData, setCsvData] = useState([]);

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    Papa.parse(file, {
      skipEmptyLines: true,
      complete: (results) => {
        setCsvData(results.data || []);
      },
    });
  };

  // ✅ Recalculate whenever month OR csvData changes
  useEffect(() => {
    if (!selectedMonth || csvData.length === 0) {
      setMonthlyTotal(0);
      return;
    }

    let total = 0;

    csvData.forEach((row) => {
      // row format: [date, category, amount, description]
      const date = (row?.[0] ?? "").trim();
      const amountStr = (row?.[2] ?? "").toString().trim();

      // skip header or bad rows
      if (!/^\d{4}-\d{2}-\d{2}$/.test(date)) return;

      if (date.startsWith(selectedMonth)) {
        const amt = parseFloat(amountStr);
        if (!isNaN(amt)) total += amt;
      }
    });

    setMonthlyTotal(total);
  }, [selectedMonth, csvData]);

  return (
    <div className="box">
      <h2>Monthly Summary (CSV)</h2>

      <input
        type="month"
        value={selectedMonth}
        onChange={(e) => setSelectedMonth(e.target.value)}
      />

      <input type="file" accept=".csv" onChange={handleFileUpload} />

      <h3>Total: ₹{monthlyTotal}</h3>
    </div>
  );
}

export default MonthlySummary;
