import { useState } from "react";
import AddExpense from "./AddExpense";
import ViewExpenses from "./ViewExpenses";
import "./style.css";

function App() {
  const [isExited, setIsExited] = useState(false);

  if (isExited) {
    return <h2 style={{ textAlign: "center" }}>Application Closed</h2>;
  }

  return (
    <div className="container">
      <h1 color="red">Personal Expense Tracker</h1>
      <AddExpense />
      <ViewExpenses />

      <button
        style={{ background: "red", marginTop: "20px" }}
        onClick={() => {
          if (window.confirm("Exit application?")) {
            setIsExited(true);
          }
        }}
      >
        Exit
      </button>
    </div>
  );
}

export default App;
