import { useState } from "react";
import Papa from "papaparse";

function MonthlySummary() {
  const [selectedMonth, setSelectedMonth] = useState("");
  const [monthlyTotal, setMonthlyTotal] = useState(0);
  const [csvData, setCsvData] = useState([]); // ✅ ADD

  const handleFileUpload = (e) => {
    const file = e.target.files[0];

    Papa.parse(file, {
      skipEmptyLines: true,
      complete: (results) => {
        setCsvData(results.data); // ✅ STORE CSV
      }
    });
  };

  const calculateMonthlyTotal = (month, data) => {
    let total = 0;

    data.forEach((row, index) => {
      // skip header row
      if (index === 0) return;

      if (row[0]?.startsWith(month)) {
        total += Number(row[2]) || 0;
      }
    });

    setMonthlyTotal(total);
  };

  return (
    <div className="box">
      <h2>Monthly Summary (CSV)</h2>

      <input
        type="month"
        value={selectedMonth}
        onChange={(e) => {
          const month = e.target.value;
          setSelectedMonth(month);
          calculateMonthlyTotal(month, csvData); // ✅ RECALCULATE
        }}
      />

      <input
        type="file"
        accept=".csv"
        onChange={handleFileUpload}
      />

      <h3>Total: ₹{monthlyTotal}</h3>
    </div>
  );
}

export default MonthlySummary;
