import { useEffect, useState } from "react";

function ViewExpenses() {
  const [expenses, setExpenses] = useState([]);
  const [filterDate, setFilterDate] = useState("");
  

  useEffect(() => {
    fetch("http://127.0.0.1:8000/api/view/")
      .then(res => res.json())
      .then(data => setExpenses(data.expenses));
  }, []);
  useEffect(() => {
  if (!month) return;

  fetch(`http://127.0.0.1:8000/api/monthly-summary/?month=${month}`)
    .then(res => res.json())
    .then(data => setMonthlyTotal(data.total));
}, [month]);

  const filteredExpenses = filterDate
    ? expenses.filter(e => e[0] === filterDate)
    : expenses;

  const total = filteredExpenses.reduce(
    (sum, e) => sum + Number(e[2]),
    0
  );

  return (
    <div className="box">
      <h2>All Expenses</h2>
    <div className="box monthly-box">
  <h3>Monthly Summary</h3>

  <input
    type="month"
    value={month}
    onChange={(e) => setMonth(e.target.value)}
  />

  <p><strong>Total:</strong> ₹{monthlyTotal}</p>
</div>

      <input
        type="date"
        value={filterDate}
        onChange={(e) => setFilterDate(e.target.value)}
      />

      <table>
        <thead>
          <tr>
            <th>Date</th>
            <th>Category</th>
            <th>Amount</th>
            <th>Description</th>
          </tr>
        </thead>
        <tbody>
          {filteredExpenses.map((e, i) => (
            <tr key={i}>
              <td>{e[0]}</td>
              <td>{e[1]}</td>
              <td>{e[2]}</td>
              <td>{e[3]}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <h3>Total Expense: ₹{total}</h3>
    </div>
  );
}

export default ViewExpenses;
